mapper.select:
SELECT e.* FROM exam e LEFT JOIN exam_class ec ON e.id = ec.exam_id where 1=1 and e.is_del = 0
and e.exam_name LIKE CONCAT('%', #{query.examName}, '%')
and e.state = #{query.status}
and ec.class_id in
#{item}
GROUP BY e.id ORDER BY e.create_time DESC

SELECT t.id,t.type_name,t.shape, SUM(CASE t.state WHEN 1 then 1 else 0 end ) questionOnNum FROM ( SELECT qt.id,qt.type_name,q.shape,q.state FROM q_question_type qt LEFT JOIN q_question q ON q.type = qt.id and q.is_del = 0 WHERe 1=1
and q.difficulty_level = #{difficultyLevel}
and q.symbol in
#{item}
GROUP BY qt.id,q.id,q.shape) t GROUP BY t.id,t.shape ORDER BY t.id,t.shape
